import axios from 'axios';
import React, { useEffect, useRef, useState } from 'react'
import { Button, Col, Row, Form, Card } from 'react-bootstrap'
import Book from './Book';

const BookPage = () => {
    const [list, setList] = useState([]);
    const [loading, setLoding] = useState(false);
    const [page, setPage] = useState(1);
    const [is_end, setIs_end] = useState(false);
    const [query, setQuery] = useState('리액트');

    const getData = async () => {
        const url = "https://dapi.kakao.com/v3/search/book?target=title";
        const config = {
            headers: { "Authorization": "KakaoAK b80880fbde422de3fd9b4a4e67c9bb54" },
            params: { query: '리액트', size: 8, page: 1 }
        }
        setLoding(true);
        const result = await axios.get(url, config)
        console.log(result);
        setList(result.data.documents);
        setLoding(false);

    }

    const onSubmit = (e) => {
        e.preventDefault();
        setPage(1);
        getData();

    }

    useEffect(() => {
        getData();
    }, []);

    if(loading) return <h1 className='text-center my-5'>로딩중.......</h1>
    return (
        <Row>
            <h1 className='text-center my-5'>도서검색</h1>
            <Row>
                <Col md={4}>
                    <Form onSubmit={onSubmit}>
                        <Form.Control
                        onChange={(e)=>setQuery(e.target.value)}
                        placeholder="검색어"/>
                    </Form>
                </Col>
            </Row>
            <Row>
                {list.map(book=>
                    <Col key={book.isbn} md={3} xs={6} className="my-2">
                        <Card>
                            <Card.Body>
                                <img src={book.thumbnail}/>
                                <div className='ellipsis'>{book.title}</div>
                                <Book book={book}/>
                            </Card.Body>
                        </Card>
                    </Col>
                )}
                <div className='text-center my-3'>
                    <Button disabled={page === 1 && true}
                        onClick={()=>setPage(page-1)}>이전</Button>
                    <span className='mx-2'>{page}</span>
                    <Button disabled={is_end && true}
                        onClick={()=>setPage(page+1)}>다음</Button>
                    </div>
            </Row>
        </Row>
    )
}

export default BookPage
